import axios from 'axios';
import { Component } from 'react';

class User extends Component {
  state = {
    User: [],
    config:{
      headers : {'authorization': `Bearer ${localStorage.getItem('token')}`}
},
  };

  //method
  componentDidMount() {
    axios
      .get('http://localhost:4000/userall')
      .then((response) => {
        this.setState({
          User: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <>
        {this.state.User.map((val) => {
          return (
            <>
              {val.username}
              {val.email}
              {val.password}
              {val.userType}
              <img src={'http://localhost:4000/' + val.avatar} alt="image" />
            </>
          );
        })}
      </>
    );
  }
}

export default User;
