import axios from 'axios';
import { Component } from 'react';
import { Navigate } from 'react-router-dom';

class User extends Component {
  state = {
    User: [],
    config: {
      headers: { authorization: `Bearer ${localStorage.getItem('token')}` },
    },
  };
  deleteUser = (e,) =>{
    e.preventDefault();
    axios.delete("http://localhost:4000/deleteuser",)
  }

  
  //method
  componentDidMount() {
    axios
      .get('http://localhost:4000/userall', this.state.config)
      .then((response) => {
        this.setState({
          User: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    if (
      localStorage.getItem('userType') &&
      localStorage.getItem('userType') !== 'Admin'
    ) {
      return <Navigate to="/login1"> </Navigate>;
    }
    return (
      <>
        {this.state.User.map((val) => {
          return (
            <>
              {val.username}
              {val.email}
              {val.password}
              {val.userType}
              <img src={'http://localhost:4000/' + val.avatar} alt="image" />
           <button className="delete" onClick={this.deleteUser.bind(this,val._id)}>Delete</button>
            </>
          );
        })}
      </>
    );
  }
}

export default User;
